            "sourceJS": "import { LiElement, html, css } from 'https://resu062.github.io/li-js/li.js';\nimport 'https://resu062.github.io/li-js/li/button/button.js';\n\ncustomElements.define('li-todo', class extends LiElement {\n    static get styles() {\n        return css`\n\t\t\t:host {\n\t\t\t\tdisplay: flex;\n\t\t\t\tflex-direction: column;\n\t\t\t}\n\t\t\th3 {\n\t\t\t\tcolor: red;\n\t\t\t}\n\t\t\t.row {\n\t\t\t\tmargin: 4px;\n\t\t\t\tpadding: 4px;\n\t\t\t\tborder: 1px solid lightgray;\n\t\t\t\tflex: 1;\n\t\t\t\tborder-radius: 2px;\n\t\t\t}\n        `;\n    }\n\n    render() {\n        return html`\n        \t<div style=\"display: flex; align-items: center; border-bottom: 1px solid gray\">\n        \t\t<h3>\nSample working todo app with data stored in a cell : </h3>\n        \t\t<div style=\"flex: 1\"></div>\n\t\t\t\t<li-button name=\"add\" @click=${this.onclickAdd} radius=\"50%\" style=\"max-width: 26px;\"></li-button>\n\t\t\t</div>\n\t\t\t${json.list.map(i => html`\n\t\t\t\t<div style=\"display: flex; align-items: center\">\n\t\t\t\t\t<div contenteditable class=\"row\" @input=${e => { i.todo = e.target.innerText }} @dblclick=${e => { i.check = !i.check; this.$update() }} style=\"text-decoration: ${i.check ? 'line-through' : 'none'}; color: ${i.check ? 'lightgray' : 'gray'}; border-color: ${i.check ? 'lightgray' : 'gray'}\">${i.todo}</div>\n\t\t\t\t\t<li-button name=\"close\"  @click=${e => this.onclickClose(e, i)}></li-button>\n\t\t\t\t</div>\n\t\t\t`)}\n        `\n    }\n\n    firstUpdated() {\n        super.firstUpdated();\n\n    }\n\n    onclickAdd(e, i) {\n    \tjson.list.push({ \"todo\" : 'new todo', 'check': false });\n        this.$update();\n    }\n    onclickClose(e, i) {\n    \tlet idx = json.list.indexOf(i);\n    \tjson.list.splice(idx, 1);\n    \tthis.$update();\n    }\n})\n\n// console.log('..... data-json: ', json)\n",
